var currentIndex = 0, slides = $('.slideecon'), slidesLength = slides.length, clicked = false;
function cycleSlides(current) { 
    var slide = $('.slideecon').eq(current);
    slides.hide().removeClass('active');
    slide.show().addClass('active');
}
cycleSlides(currentIndex);
$(document).ready(function(){
    if(slidesLength == 1) {
        $('.btn-landing-close').show();
        $('.v4-btn-next').hide();
    } else {
        $('.v4-btn-next').show();
    }
    $('.v4-btn-prev').hide(); 
    $('.v4-btn-next').on('click', function() {
        $('.v4-btn-prev').show();
        currentIndex += 1;
        cycleSlides(currentIndex);
        var currenState = parseInt(slidesLength) - parseInt(currentIndex);
        if(currenState == 1) {
            $(".btn-landing-close").show();
            $(".v4-btn-next").hide();
        } else {
            $(".btn-landing-close").hide();
            $(".v4-btn-next").show();
            $('.v4-btn-next').attr("disabled", false);
        }
        cycleSlides(currentIndex);
    });
    $('.v4-btn-prev').on('click', function() {
        $(".btn-landing-close").hide();
        currentIndex -= 1;
        if (currentIndex == 0) {
            $('.v4-btn-prev').hide();
            $('.v4-btn-next').show();
        } else if (currentIndex >= slidesLength - 2) {
            $('.v4-btn-next').show();
        } 
        cycleSlides(currentIndex);
    });
    $(".btn-landing-close").on("click", function(){
        if ($(".kbaru-modal").length > 0) {
            $(".kbaru-modal").remove();
            return false;
        }
    });
});